Queries.getNumberFollowingById:
SELECT COUNT(*)
FROM following
WHERE follower_id = ?;

Queries.getNumberFollowersById:
SELECT COUNT(*)
FROM following
WHERE following_id = ?;

Queries.getGameRequestsByUserAndAccepted:
SELECT *
FROM gamerequest
WHERE riftee_id = ? AND accepted = ?;

Queries.createUser:
INSERT INTO usertable
(first_name, last_name, gender, is_private, is_suspended, rift_tag)
VALUES(?, ?, ?, false, false, ?);

Queries.createGame:
INSERT INTO riftergame
(host_id, num_slots, expiration_time, game_cost, method_of_contact, game_type, title, hits, game_duration, game_time)
VALUES(?, ?, ?, ?, ?, ?, ?, ?, ?, ?);
